  },
  ariaDescribedby: {
    type: String
  }
} as const

export const Props = {
  type: {
    type: String as PropType<ChartType>,
    required: true
  },
  destroyDelay: {
    type: Number,
    default: 0 // No delay by default
  },
  ...CommonProps,
  ...A11yProps
} as const
